import React, {useState} from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"

const App = () => {
  const [state, setState] = useState({
    orders: [],
    items: [
      {
        id: 1,
        title: 'Мотоцикл MINSK D4 125',
        img: 'Мотоцикл MINSK D4 125.jpg',
        desc: '(красный, чёрный, графитовый, зелёный)',
        category: 'motorbike',
        price: '4 200.00'
      },
      {
        id: 2,
        title: 'Мопед MINSK D4 50',
        img: 'Мопед MINSK D4 50.jpg',
        desc: '(зелёный, бордовый, чёрный)',
        category: 'moped',
        price: '3 200.00'
      },
      {
        id: 3,
        title: 'Мотоцикл MINSK X 250 (оранжевый, чёрный, белый камуфляж)',
        img: 'Мотоцикл MINSK X 250.jpg',
        desc: '(оранжевый, чёрный, белый камуфляж)',
        category: 'motorbike',
        price: '5 700.00'
      },
      {
        id: 4,
        title: 'Мотоцикл MINSK С4 250',
        img: 'Мотоцикл MINSK С4 250.jpg',
        desc: '(бирюзовый, бордовый, бежевый)',
        category: 'motorbike',
        price: '7 900.00'
      },
      {
        id: 5,
        title: 'Мотоцикл MINSK SCR 250 (серый, чёрный)',
        img: 'Мотоцикл MINSK SCR 250.jpg',
        desc: 'Нет описания.',
        category: 'motorbike',
        price: '8 800.00'
      },
      {
        id: 6,
        title: 'Скутер MINSK Vesna 125 (белый, чёрный)',
        img: 'Скутер MINSK Vesna 125.jpg',
        desc: 'Нет описания.',
        category: 'scooter',
        price: '4 629.24'
      },
      {
        id: 7,
        title: 'Скутер Vesna 50 (синий, красный)',
        img: 'Скутер Vesna 50.jpg',
        desc: 'Нет описания.',
        category: 'scooter',
        price: '4 484.57'
      },
      {
        id: 8,
        title: 'Квадроцикл Lis 125',
        img: 'Квадроцикл Lis 125.jpg',
        desc: 'Нет описания.',
        category: 'ATV',
        price: '4 995.00'
      },
      {
        id: 9,
        title: 'Квадроцикл Dzik 200',
        img: 'Квадроцикл Dzik 200.jpg',
        desc: 'Нет описания.',
        category: 'ATV',
        price: '8 862.00'
      }
    ]
  })

  const deleteOrder = (id) => {
    setState({orders: state.orders.filter(el => el.id !== id)})
  }

  const addToOrder = (item) => {
    let isInArray = false
    state.orders.forEach(el => {     
      if(el.id === item.id)
        isInArray = true
    })
    if(!isInArray)
      setState(prevState => ({...prevState, orders: [...state.orders, item]}))
  }

  return (
    <div className="wrapper">
      <Header orders={state.orders} onDelete={deleteOrder} />
      <Items items={state.items} onAdd={addToOrder} />
      <Footer />
    </div>
  )
}

export default App;
