import React from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [],
      items: [
        {
          id: 1,
          title: 'Мотоцикл Minsk CX 650',
          img: 'Minsk_CX_650.jpg',
          desc: 'Мотоцикл Minsk CX 650 (или Minsk X-Ride 650) является современной интерпретацией классического байка.',
          category: 'motorbike',
          price: '15 000.00'
        },
        {
          id: 2,
          title: 'Мотоцикл MINSK С4 250',
          img: 'MINSK_С4_250.jpg',
          desc: 'Нет описания.',
          category: 'motorbike',
          price: '7 900.00'
        },
        {
          id: 3,
          title: 'Мотоцикл MINSK SCR 250',
          img: 'MINSK_SCR_250.jpg',
          desc: 'Нет описания.',
          category: 'motorbike',
          price: '8 800.00'
        }
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }

  addToOrder(item) {
    this.setState({orders: [...this.state.orders, item] }, () => {
      console.log(this.state.orders)
    })
  }

  render() {
  return (
    <div className="wrapper">
      <Header />
      <Items items={this.state.items} onAdd={this.addtoOrder} />
      <Footer />
    </div>
  )
  }
}

export default App;
