import React, {useState} from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/Items"

const App = () => {
  const [state, setState] = useState({
    orders: [],
    items: [
      {
        id: 1,
        title: 'Мотоцикл Minsk CX 650',
        img: 'Minsk_CX_650.jpg',
        desc: 'Мотоцикл Minsk CX 650 (или Minsk X-Ride 650) является современной интерпретацией классического байка.',
        category: 'motorbike',
        price: '15 000.00'
      },
      {
        id: 2,
        title: 'Мотоцикл MINSK С4 250',
        img: 'MINSK_С4_250.jpg',
        desc: 'Нет описания.',
        category: 'motorbike',
        price: '7 900.00'
      },
      {
        id: 3,
        title: 'Мотоцикл MINSK SCR 250',
        img: 'MINSK_SCR_250.jpg',
        desc: 'Нет описания.',
        category: 'motorbike',
        price: '8 800.00'
      }
    ]
    })

  const addToOrder = (item) => {
    setState({orders: [...state.orders, item] }, () => {
      console.log(state.orders)
    })
  }

  console.log(state)

  return (
    <div className="wrapper">
      <Header />
      <Items items={state.items} onAdd={addToOrder} />
      <Footer />
    </div>
  )
}

export default App;

