import React from 'react'

export const Item = (props) => {
    return (
        <div className='item'>
                <img 
                    src={"./img/" + props.item.img}
                    alt="" 
                />
                <h2>{props.item.title}</h2>
                <p>{props.item.desc}</p>
                <b>{props.item.price.toLocaleString('ru-RU', { minimumFractionDigits: 2, maximumFractionDigits: 2 }).replace(/,/g, ' ')} BYN</b>
                <div className='add-to-cart' onClick={() => props.onAdd(props.item)}>+</div>
            </div>
    )
}

export default Item
